$(function() {altair_form_validation.init();});altair_form_validation = {init: function() {if($('#form_validation').length>0){var $formValidate = $('#form_validation');$formValidate.parsley().on('form:validated',function() {altair_md.update_input($formValidate.find('.md-input-danger'));}).on('field:validated',function(parsleyField) {if($(parsleyField.$element).hasClass('md-input')) {altair_md.update_input( $(parsleyField.$element) );}});}}};